SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_crm_esp_medico (cd_medico_p text) RETURNS varchar AS $body$
DECLARE


ds_crm_w		        varchar(255)	:= '';
ds_retorno_w			varchar(4000);

c01 CURSOR FOR
	SELECT 	nr_rqe
	from 	medico_especialidade a
	where 	cd_pessoa_fisica	= cd_medico_p
	and     (nr_rqe IS NOT NULL AND nr_rqe::text <> '');


BEGIN

if (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') then
	open	c01;
	loop
	fetch	c01 into ds_crm_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
			ds_retorno_w := ds_retorno_w ||ds_crm_w || ';';
		end;
	end loop;
	close c01;
end if;

ds_retorno_w	:= substr(ds_retorno_w, 1, length(ds_retorno_w) - 1);

RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
