SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_crm_medico (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


	ds_retorno_w	        varchar(20);
	param_w                 varchar(1);
	cd_uf_logado_w          cep_localidade.cd_unidade_federacao%type;
	ie_get_crm_default_w    varchar(1) := 'S';


BEGIN

	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
			param_w := Obter_Valor_Param_Usuario(911, 56, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento);

			if (param_w = 'S') then
					select  max(c.cd_unidade_federacao)
					into STRICT    cd_uf_logado_w
					from    cep_localidade c,
							estabelecimento e
					where   c.cd_localidade = to_char(e.cd_localidade)
					and     e.cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

					select  max(nr_crm)
					into STRICT    ds_retorno_w
					from    crm_estado
					where   cd_pessoa_fisica = cd_pessoa_fisica_p
					and     cd_unidade_federacao = cd_uf_logado_w;

					if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
							ie_get_crm_default_w := 'N';
					end if;
			end if;

			if (ie_get_crm_default_w = 'S') then
					select  nr_crm
					into STRICT    ds_retorno_w
					from    medico
					where   cd_pessoa_fisica = cd_pessoa_fisica_p;
			end if;
	end if;

    RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
