SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_crm_nm_medico (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

				
ds_retorno_w		varchar(100);
nr_crm_w			varchar(20);
nm_pf			varchar(100);


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin

	select	nr_crm
	into STRICT	nr_crm_w
	from	medico
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p;
	
	if (nr_crm_w IS NOT NULL AND nr_crm_w::text <> '') then
		begin
		
		select 	a.nm_pessoa_fisica
		into STRICT	nm_pf
		from 	pessoa_fisica a, medico b 
		where 	a.cd_pessoa_fisica 	= b.cd_pessoa_fisica 
		and 	a.cd_pessoa_fisica 	= cd_pessoa_fisica_p;

		if (nm_pf IS NOT NULL AND nm_pf::text <> '') then			
			ds_retorno_w := nr_crm_w || ' - ' || nm_pf;
		end if;
		end;
	end if;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
