SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cst_regra_item_nf ( nr_sequencia_p bigint, cd_material_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_classif_trib_w		regra_cst_item_nf.nr_seq_classif_trib%type;
cd_grupo_material_w		grupo_material.cd_grupo_material%type;
cd_subgrupo_material_w		subgrupo_material.cd_subgrupo_material%type;
cd_classe_material_w		classe_material.cd_classe_material%type;
cd_area_proced_w		regra_cst_item_nf.cd_area_procedimento%type;
cd_espec_proced_w		regra_cst_item_nf.cd_especialidade%type;
cd_grupo_proced_w		regra_cst_item_nf.cd_grupo_proc%type;
cd_tipo_pessoa_w		pessoa_juridica.cd_tipo_pessoa%type;
cd_cnpj_w			pessoa_juridica.cd_cgc%type;

/*Busca CST para Material*/

c01 CURSOR FOR
SELECT	nr_seq_classif_trib
from	regra_cst_item_nf
where	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and	coalesce(cd_tipo_pessoa, cd_tipo_pessoa_w)	= cd_tipo_pessoa_w
and (coalesce(cd_material, cd_material_p) 			= cd_material_p or cd_material_p = 0)
order by
	coalesce(cd_material, 0),
	coalesce(cd_classe_material, 0),
	coalesce(cd_subgrupo_material, 0),
	coalesce(cd_grupo_material, 0),
	coalesce(cd_tipo_pessoa,0);

/*Busca CST para Procedimento*/

c02 CURSOR FOR
SELECT	nr_seq_classif_trib
from	regra_cst_item_nf
where	coalesce(cd_procedimento, cd_procedimento_p)	= cd_procedimento_p
and	((coalesce(cd_procedimento::text, '') = '') or (coalesce(ie_origem_proced, ie_origem_proced_p)	= ie_origem_proced_p))
and	coalesce(cd_area_procedimento, cd_area_proced_w)	= cd_area_proced_w
and	coalesce(cd_especialidade, cd_espec_proced_w)	= cd_espec_proced_w
and	coalesce(cd_grupo_proc, cd_grupo_proced_w)	= cd_grupo_proced_w
and	coalesce(cd_tipo_pessoa, cd_tipo_pessoa_w)	= cd_tipo_pessoa_w
order by
	coalesce(cd_area_procedimento,0),
	coalesce(cd_especialidade,0),
	coalesce(cd_grupo_proc,0),
	coalesce(cd_procedimento,0),
	coalesce(cd_tipo_pessoa,0);


BEGIN

select	coalesce(cd_cgc_emitente, cd_cgc)
into STRICT	cd_cnpj_w
from	nota_fiscal
where	nr_sequencia = nr_sequencia_p;

select	cd_tipo_pessoa
into STRICT	cd_tipo_pessoa_w
from	pessoa_juridica
where	cd_cgc = cd_cnpj_w;

if (cd_material_p > 0) then

	select	a.cd_grupo_material,
		a.cd_subgrupo_material,
		a.cd_classe_material
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w
	from	estrutura_material_v a
	where	a.cd_material = cd_material_p;

	open C01;
	loop
	fetch C01 into
		nr_seq_classif_trib_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		nr_seq_classif_trib_w := nr_seq_classif_trib_w;
		end;
	end loop;
	close C01;


elsif (cd_procedimento_p > 0) and (ie_origem_proced_p > 0) then

	select	coalesce(max(cd_area_procedimento),0),
		coalesce(max(cd_especialidade),0),
		coalesce(max(cd_grupo_proc),0)
	into STRICT	cd_area_proced_w,
		cd_espec_proced_w,
		cd_grupo_proced_w
	from	estrutura_procedimento_v
	where	cd_procedimento = cd_procedimento_p
	and	ie_origem_proced = ie_origem_proced_p;

	open C02;
	loop
	fetch C02 into
		nr_seq_classif_trib_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		nr_seq_classif_trib_w := nr_seq_classif_trib_w;
		end;
	end loop;
	close C02;

end if;

return	nr_seq_classif_trib_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_cst_regra_item_nf ( nr_sequencia_p bigint, cd_material_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) FROM PUBLIC;

