CREATE OR REPLACE FUNCTION obter_curva_abc_estab ( cd_estabelecimento_p bigint, cd_material_p bigint, ie_curva_grupo_p text, dt_mesano_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_curva_w				varchar(1);
cd_material_w				integer;


BEGIN

select	coalesce(max(cd_material_estoque), cd_material_p)
into STRICT	cd_material_w
from 	material
where 	cd_material	= cd_material_p;

select	coalesce(max(CASE WHEN ie_curva_grupo_p='S' THEN ie_curva_abc_grupo  ELSE ie_curva_abc END ),'X')
into STRICT		ie_curva_w
from		Material_abc
where		cd_estabelecimento		= cd_estabelecimento_p
and		cd_material			= cd_material_w
and		dt_mesano_Referencia		= (
		SELECT max(dt_mesano_referencia)
		from	Material_abc
		where	cd_estabelecimento	= cd_estabelecimento_p
		and	cd_material		= cd_material_w
		and	dt_mesano_Referencia	<= dt_mesano_Referencia_p);

Return ie_curva_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
