CREATE OR REPLACE FUNCTION obter_curva_abc_local ( cd_material_p bigint, ie_curva_grupo_p text, cd_local_estoque_p bigint, dt_mesano_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_curva_w		varchar(1);
cd_material_w		integer;


BEGIN

select	coalesce(max(cd_material_estoque), cd_material_p)
into STRICT	cd_material_w
from 	material
where 	cd_material	= cd_material_p;

select	coalesce(max(CASE WHEN ie_curva_grupo_p='S' THEN ie_curva_abc_grupo  ELSE ie_curva_abc END ),'X')
into STRICT	ie_curva_w
from	material_abc_local
where	cd_material	= cd_material_w
and	cd_local_estoque	= cd_local_estoque_p
and	dt_mesano_referencia = (
	SELECT	max(dt_mesano_referencia)
	from	material_abc_local
	where	cd_material		= cd_material_w
	and	cd_local_estoque		= cd_local_estoque_p
	and	dt_mesano_Referencia	<= dt_mesano_referencia_p);

Return ie_curva_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
