CREATE OR REPLACE FUNCTION obter_custo_medio_consig ( cd_estabelecimento_p bigint, cd_local_estoque_p bigint, cd_material_p bigint, cd_fornecedor_p text, dt_mes_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_custo_medio_w		double precision;
cd_material_w		integer;
dt_inicio_w		timestamp;
dt_final_w		timestamp;


BEGIN

select	coalesce(cd_material_estoque, cd_material)
into STRICT	cd_material_w
from	material
where	cd_material = cd_material_p;

select	coalesce(avg(vl_custo_medio),0)
into STRICT	vl_custo_medio_w
from	fornecedor_mat_consignado
where	dt_mesano_referencia	= ESTABLISHMENT_TIMEZONE_UTILS.startOfMonth(dt_mes_referencia_p)
and	cd_material		= cd_material_w
and	cd_estabelecimento		= coalesce(cd_estabelecimento_p, cd_estabelecimento)
and	cd_local_estoque		= coalesce(cd_local_estoque_p, cd_local_estoque)
and	cd_fornecedor		= coalesce(cd_fornecedor_p, cd_fornecedor)
and	coalesce(vl_custo_medio,0) <> 0;

return vl_custo_medio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
