CREATE OR REPLACE FUNCTION obter_custo_medio_item_solic ( nr_solic_compra_p bigint, nr_item_solic_p bigint) RETURNS bigint AS $body$
DECLARE

 
vl_custo_medio_w			double precision;
cd_material_w			integer;
cd_estabelecimento_w		smallint;
qt_conversao_w			double precision;
cd_unidade_medida_solic_w		varchar(30);
cd_unidade_medida_estoque_w	varchar(30);
dt_solicitacao_compra_w		timestamp;


BEGIN 
 
select	b.cd_material, 
	b.cd_unidade_medida_compra, 
	obter_dados_material_estab(b.cd_material,a.cd_estabelecimento,'UME'), 
	a.cd_estabelecimento, 
	a.dt_solicitacao_compra 
into STRICT	cd_material_w, 
	cd_unidade_medida_solic_w, 
	cd_unidade_medida_estoque_w, 
	cd_estabelecimento_w, 
	dt_solicitacao_compra_w 
from	solic_compra a, 
	solic_compra_item b 
where	a.nr_solic_compra = b.nr_solic_compra 
and	a.nr_solic_compra = nr_solic_compra_p 
and	b.nr_item_solic_compra = nr_item_solic_p;
 
select	obter_custo_medio_material(cd_estabelecimento_w, trunc(dt_solicitacao_compra_w,'mm'), cd_material_w) 
into STRICT	vl_custo_medio_w
;
 
if (cd_unidade_medida_solic_w <> cd_unidade_medida_estoque_w) then /* unidade de compra não é igual a de estoque*/
 
	begin 
 
	select	coalesce(max(qt_conv_compra_estoque),1) 
	into STRICT	qt_conversao_w 
	from	material 
	where	cd_material = cd_material_w;
 
	vl_custo_medio_w	:= (vl_custo_medio_w * qt_conversao_w);
 
	end;
end if;
 
return	vl_custo_medio_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
