CREATE OR REPLACE FUNCTION obter_custo_medio_material_log ( cd_estabelecimento_p bigint, dt_mesano_referencia_p timestamp, cd_material_p bigint, cd_operacao_estoque_p bigint, vl_total_entrada_p bigint, qt_estoque_ant_p bigint, qt_entrada_p bigint, nr_movimento_estoque_ant_p bigint) RETURNS bigint AS $body$
DECLARE

		
vl_custo_medio_w		saldo_estoque_log.vl_custo_medio%type;
vl_estoque_ant_w		bigint;
parcial_w 			bigint;


BEGIN
	select coalesce(max(vl_custo_medio),0)
	into STRICT	vl_custo_medio_w
	from saldo_estoque_log
	where cd_material = cd_material_p
	and	cd_estabelecimento = coalesce(cd_estabelecimento_p, cd_estabelecimento)
	and dt_atualizacao = dt_mesano_referencia_p;

	if (cd_operacao_estoque_p = 11) then 	
		select qt_estoque * obter_custo_medio_material(wheb_usuario_pck.get_cd_estabelecimento, dt_mesano_referencia, cd_material)
		into STRICT vl_estoque_ant_w
		from w_razao_estoque
		where nr_movimento_estoque = nr_movimento_estoque_ant_p;

		parcial_w := vl_estoque_ant_w + vl_total_entrada_p;
		vl_custo_medio_w := parcial_w / (qt_estoque_ant_p + qt_entrada_p);			
	end if;

return	coalesce(vl_custo_medio_w,0);
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
