CREATE OR REPLACE FUNCTION obter_custo_medio_material_nf ( cd_estabelecimento_p bigint, dt_referencia_p timestamp, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


vl_custo_w	double precision;
dt_inicio_w	timestamp;
dt_fim_w		timestamp;



BEGIN
vl_custo_w		:= 0;
dt_inicio_w		:= trunc(dt_referencia_p,'mm');
dt_fim_w			:= fim_mes(dt_referencia_p);

begin
select	dividir(sum(a.vl_liquido), sum(a.qt_item_estoque))
into STRICT	vl_custo_w
from	nota_fiscal b,
	nota_fiscal_item a
where	a.nr_sequencia		= b.nr_sequencia
and	a.cd_material		= cd_material_p
and	b.cd_estabelecimento	= cd_estabelecimento_p
and	b.ie_situacao		= 1
and	substr(obter_se_nota_entrada_saida(b.nr_sequencia),1,1) = 'E'
and	b.dt_atualizacao_estoque between dt_inicio_w and dt_fim_w;
exception when others then
	vl_custo_w	:= 0;
end;

return vl_custo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
