CREATE OR REPLACE FUNCTION obter_custo_medio_material ( cd_estabelecimento_p bigint, dt_mesano_referencia_p timestamp, cd_material_p bigint, ie_consignado_p text default null) RETURNS bigint AS $body$
DECLARE


vl_custo_medio_w		double precision;
cd_material_w		integer;
ie_custo_medio_ant_w	varchar(1);
ie_consignado_w		varchar(1);

C01 CURSOR FOR
	SELECT	coalesce(vl_custo_medio,0)
	from	saldo_estoque
	where	cd_material	= cd_material_w
	and	cd_estabelecimento	= coalesce(cd_estabelecimento_p, cd_estabelecimento)
	order by	dt_mesano_referencia desc,
		vl_custo_medio desc;

BEGIN

select	coalesce(cd_material_estoque, cd_material),
	ie_consignado
into STRICT	cd_material_w,
	ie_consignado_w
from	material
where	cd_material = cd_material_p;

if (ie_consignado_p = 1) then
	ie_consignado_w := '1';
end if;

if (ie_consignado_w = '1') then
	begin
	vl_custo_medio_w := obter_custo_medio_consig( cd_estabelecimento_p, null, cd_material_p, null, dt_mesano_referencia_p);
	end;
else
	begin
	select	coalesce(max(vl_custo_medio),0)
	into STRICT	vl_custo_medio_w
	from	saldo_estoque
	where	dt_mesano_referencia	= ESTABLISHMENT_TIMEZONE_UTILS.startOfMonth(dt_mesano_referencia_p)
	and	cd_material		= cd_material_w
	and	cd_estabelecimento	= coalesce(cd_estabelecimento_p, cd_estabelecimento);

	if (vl_custo_medio_w = 0) then
		begin
		select	coalesce(max(ie_custo_medio_ant),'N')
		into STRICT	ie_custo_medio_ant_w
		from	parametro_estoque
		where	cd_estabelecimento = coalesce(cd_estabelecimento_p, cd_estabelecimento);
		
		if (ie_custo_medio_ant_w = 'S') then
			begin
			open C01;
			loop
			fetch C01 into	
				vl_custo_medio_w;
			exit when ((C01%notfound) or (vl_custo_medio_w > 0));
			end loop;
			close C01;
			end;
		end if;
		end;
	end if;
	end;
end if;

return	vl_custo_medio_w;
end;
$body$
