CREATE OR REPLACE FUNCTION obter_custo_medio_mat_conver ( cd_estabelecimento_p bigint, dt_mesano_referencia_p timestamp, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE



vl_custo_medio_w		double precision;
cd_material_estoque_w		bigint;
QT_CONV_ESTOQUE_CONSUMO_w	double precision;
QT_CONV_COMPRA_ESTOQUE_W	double precision;


BEGIN

select	cd_material_estoque
into STRICT	cd_material_estoque_w
from	material
where	cd_material	= cd_material_p;

select	QT_CONV_ESTOQUE_CONSUMO,
	QT_CONV_COMPRA_ESTOQUE
into STRICT	QT_CONV_ESTOQUE_CONSUMO_w,
	QT_CONV_COMPRA_ESTOQUE_w
from	material
where	cd_material	= cd_material_estoque_w;

select	coalesce(max(vl_custo_medio),0)
into STRICT	vl_custo_medio_w
from	saldo_estoque
where	trunc(dt_mesano_referencia, 'month')	= trunc(dt_mesano_referencia_p, 'month')
and	cd_material	= cd_material_estoque_w
and	cd_estabelecimento = coalesce(cd_estabelecimento_p,cd_estabelecimento);

if (vl_custo_medio_w = 0) then
	Select	coalesce(obter_dados_ult_compra_data(cd_estabelecimento_p,cd_material_p,null,clock_timestamp(),0,'VI'),0)
	into STRICT	vl_custo_medio_w
	;
end if;

return	dividir(dividir(vl_custo_medio_w,QT_CONV_COMPRA_ESTOQUE_w),QT_CONV_ESTOQUE_CONSUMO_w);

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
