SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_custo_orc_pac (nr_seq_orcamento_p bigint) RETURNS bigint AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	coalesce(vl_custo,0)
	from	orcamento_paciente_proc
	where	nr_sequencia_orcamento	= nr_seq_orcamento_p
	
union all

	SELECT	coalesce(vl_custo,0)
	from	orcamento_paciente_mat
	where	nr_sequencia_orcamento	= nr_seq_orcamento_p;

vl_custo_total_w	double precision	:= 0;
vl_custo_w		double precision;


BEGIN




if (nr_seq_orcamento_p IS NOT NULL AND nr_seq_orcamento_p::text <> '') then
	open C01;
	loop
	fetch C01 into
		vl_custo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		vl_custo_total_w	:= vl_custo_total_w + vl_custo_w;
		end;
	end loop;
	close C01;

end if;


Return vl_custo_total_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
