CREATE OR REPLACE FUNCTION obter_custo_reposicao_material ( cd_material_p bigint, dt_inicio_p timestamp, dt_final_p timestamp, cd_estab_p bigint default null) RETURNS bigint AS $body$
DECLARE


vl_custo_w		double precision    	:= 0;
nr_sequencia_w		bigint	:= 0;
dt_final_w		timestamp;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;


BEGIN
dt_final_w := (trunc(coalesce(dt_final_p, clock_timestamp()),'dd') + 86399/86400);

if (cd_estab_p IS NOT NULL AND cd_estab_p::text <> '') then
	cd_estabelecimento_w := cd_estab_p;
else
	cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;
end if;

select	max(a.nr_sequencia)
into STRICT	nr_sequencia_w
from	Nota_fiscal_item b,
	Nota_Fiscal a
where	a.nr_sequencia		= b.nr_sequencia
and	cd_material_estoque	= cd_material_P
and	a.ie_acao_nf		= '1'
and	a.ie_situacao		= '1'
and	a.cd_estabelecimento = cd_estabelecimento_w
and	b.dt_atualizacao between dt_inicio_p and dt_final_w;

select coalesce(avg(dividir(vl_liquido,qt_item_estoque)),0)
into STRICT 	vl_custo_w
from 	Nota_fiscal_item
where cd_material_estoque	= cd_material_P
and	nr_sequencia 		= nr_sequencia_w
and	cd_estabelecimento = cd_estabelecimento_w;

Return vl_custo_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
