CREATE OR REPLACE FUNCTION obter_custo_var_proc ( cd_estabelecimento_p bigint, ie_tipo_atendimento_p bigint, nr_interno_conta_p bigint, nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE

 
vl_custo_w				double precision;
vl_medico_w				double precision;
vl_materiais_w			double precision;
vl_custo_operacional_w		double precision;
cd_procedimento_w			bigint;
ie_origem_proced_w			bigint;
cd_area_proc_w			bigint;
cd_espec_proc_w			bigint;
cd_grupo_proc_w			bigint;
cd_setor_atendimento_w		integer;
ie_honorario_w			varchar(01);
ie_material_w				varchar(01);
ie_custo_oper_w			varchar(01);

c010 CURSOR FOR 
SELECT coalesce(ie_honorario,'S'), 
	coalesce(ie_material,'S'), 
	coalesce(ie_custo_oper,'S') 
from 	regra_custo_variavel 
where	cd_estabelecimento					= cd_estabelecimento_p 
and	coalesce(cd_setor_atendimento,cd_setor_atendimento_w)= cd_setor_atendimento_w 
and	coalesce(ie_tipo_atendimento,ie_tipo_atendimento_p)	= ie_tipo_atendimento_p 
and	coalesce(cd_procedimento,cd_procedimento_w)		= cd_procedimento_w 
and	coalesce(ie_origem_proced,ie_origem_proced_w)		= ie_origem_proced_w 
and	coalesce(cd_grupo_proced, cd_grupo_proc_w)		= cd_grupo_proc_w 
and	coalesce(cd_especial_proced,cd_espec_proc_w)		= cd_espec_proc_w 
and	coalesce(cd_area_proced, cd_area_proc_w)		= cd_area_proc_w 
order by coalesce(cd_procedimento, 0), 
	 coalesce(ie_origem_proced, 0), 
	 coalesce(cd_grupo_proced, 0), 
	 coalesce(cd_especial_proced, 0), 
	 coalesce(cd_area_proced, 0);


BEGIN 
 
select	coalesce(a.vl_medico,0) + coalesce(a.vl_auxiliares,0) + coalesce(a.vl_anestesista,0), 
	coalesce(a.vl_materiais,0), 
	coalesce(a.vl_custo_operacional,0), 
	a.cd_procedimento, 
	a.ie_origem_proced, 
	b.cd_area_procedimento, 
	b.cd_especialidade, 
	b.cd_grupo_proc, 
	a.cd_setor_atendimento 
into STRICT	vl_medico_w, 
	vl_materiais_w, 
	vl_custo_operacional_w, 
	cd_procedimento_w, 
	ie_origem_proced_w, 
	cd_area_proc_w, 
	cd_espec_proc_w, 
	cd_grupo_proc_w, 
	cd_setor_atendimento_w 
from	estrutura_procedimento_v b, 
	atendimento_paciente d, 
	conta_paciente c, 
	conta_paciente_resumo a 
where	a.nr_interno_conta	= nr_interno_conta_p 
and	a.nr_interno_conta	= c.nr_interno_conta 
and	c.nr_atendimento	= d.nr_atendimento 
and	a.nr_sequencia	= nr_sequencia_p 
and	a.cd_procedimento	= b.cd_procedimento 
and	a.ie_origem_proced	= b.ie_origem_proced;
 
ie_honorario_w			:= 'N';
ie_material_w				:= 'N';
ie_custo_oper_w			:= 'N';
open c010;
loop 
fetch c010 into	 
	ie_honorario_w, 
	ie_material_w, 
	ie_custo_oper_w;
EXIT WHEN NOT FOUND; /* apply on c010 */
	ie_honorario_w		:= ie_honorario_w;
end loop;
close c010;
vl_custo_w			:= 0;
if (ie_honorario_w = 'S') then 
	vl_custo_w		:= vl_custo_w + vl_medico_w;
end if;
if (ie_material_w = 'S') then 
	vl_custo_w		:= vl_custo_w + vl_materiais_w;
end if;
if (ie_custo_oper_w = 'S') then 
	vl_custo_w		:= vl_custo_w + vl_custo_operacional_w;
end if;
 
return vl_custo_w;	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
