CREATE OR REPLACE FUNCTION obter_dados_acomod_diaria (nr_atendimento_p bigint, nr_seq_interno_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
cd_tipo_acomodacao_w	smallint;


BEGIN

if (ie_tipo_p = 'A') then --Acomodação
	select	coalesce(max(cd_tipo_acomodacao),0)
	into STRICT	cd_tipo_acomodacao_w
	from 	atend_acomod_diaria
	where 	nr_atendimento = nr_atendimento_p
	and 	nr_seq_interno = nr_seq_interno_p;

	if (cd_tipo_acomodacao_w > 0) then

		select	substr(coalesce(max(ds_tipo_acomodacao),Wheb_mensagem_pck.get_texto(800043)),1,255)
		into STRICT	ds_retorno_w
		from 	tipo_acomodacao
		where 	cd_tipo_acomodacao = cd_tipo_acomodacao_w;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
