SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_adiant_pago (nr_adiantamento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
	'O'	- Título que originou o adiantamento - NR_TITULO_ORIGINAL
	'TG'	- Título gerado a partir do adiantamento
	'DTL'            -  Data de liquidação do título gerado
*/
ds_retorno_w	varchar(255);


BEGIN

ds_retorno_w		:= null;
if (ie_opcao_p = 'O') then
	select	max(nr_titulo_original)
	into STRICT	ds_retorno_w
	from	adiantamento_pago
	where	nr_adiantamento	= nr_adiantamento_p;
end if;

if (ie_opcao_p = 'TG') then
	select	max(a.nr_titulo)
	into STRICT	ds_retorno_w
	from	titulo_pagar a
	where	a.nr_adiant_pago	= nr_adiantamento_p;
end if;

if (ie_opcao_p = 'DTL') then
	select	max(a.dt_liquidacao)
	into STRICT	ds_retorno_w
	from	titulo_pagar a
	where	a.nr_adiant_pago	= nr_adiantamento_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
