CREATE OR REPLACE FUNCTION obter_dados_adiant_titulo (nr_titulo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
VL	Valor adiantamento
*/
vl_adiantamento_w	double precision;
ds_retorno_w		varchar(255);


BEGIN
if (ie_opcao_p	= 'VL') then
	select	coalesce(sum(a.vl_adiantamento),0)
	into STRICT	vl_adiantamento_w
	from	titulo_pagar_adiant a
	where	a.nr_titulo	= nr_titulo_p;

	ds_retorno_w	:= vl_adiantamento_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
