CREATE OR REPLACE FUNCTION obter_dados_aditivo_contr ( nr_sequencia_p bigint, dt_inicio_p timestamp, dt_final_p timestamp, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE

/* ie_opcao_p
0 - dt_assinatura
1 - dt_publicacao
2 - ds_objeto
*/
nr_seq_aditivo_w			bigint;
dt_assinatura_w			varchar(25);
dt_publicacao_w			varchar(25);
ds_objeto_w			varchar(4000);
ds_retorno_w			varchar(4000);


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_aditivo_w
from	contrato
where	nr_seq_contrato_atual = nr_sequencia_p
and	ie_classificacao = 'AD'
and	ie_situacao = 'A'
and	trunc(dt_inicio,'dd') between trunc(dt_inicio_p,'dd') and trunc(dt_final_p,'dd');

if (nr_seq_aditivo_w > 0) then
	select	replace(to_char(obter_vigencia_contrato(a.nr_sequencia, 0, 'I'),'dd/mm/yyyy'),'/','') dt_assinatura,
		replace(to_char(a.dt_publicacao,'dd/mm/yyyy'),'/','') dt_publicacao,
		substr(elimina_acentuacao(a.ds_objeto_contrato),1,4000) ds_objeto
	into STRICT	dt_assinatura_w,
		dt_publicacao_w,
		ds_objeto_w
	from	contrato a
	where	nr_sequencia = nr_seq_aditivo_w;
else

	select	replace(to_char(obter_vigencia_contrato(a.nr_sequencia, 0, 'I'),'dd/mm/yyyy'),'/','') dt_assinatura,
		replace(to_char(a.dt_publicacao,'dd/mm/yyyy'),'/','') dt_publicacao,
		substr(elimina_acentuacao(a.ds_objeto_contrato),1,4000) ds_objeto
	into STRICT	dt_assinatura_w,
		dt_publicacao_w,
		ds_objeto_w
	from	contrato a
	where	nr_sequencia = nr_sequencia_p;
end if;

if (ie_opcao_p = 0) then
	ds_retorno_w := dt_assinatura_w;
elsif (ie_opcao_p = 1) then
	ds_retorno_w := dt_publicacao_w;
elsif (ie_opcao_p = 2) then
	ds_retorno_w := ds_objeto_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
