SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_agenda_apae ( nr_seq_agenda_p bigint, ie_opacao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(255);


BEGIN 
 
if (ie_opacao_p = 'PA') then 
	select	substr(obter_nome_pf(cd_pessoa_fisica),1,255) 
	into STRICT	ds_retorno_w 
	from	agenda_paciente 
	where	nr_sequencia	=	nr_seq_agenda_p;
elsif (ie_opacao_p = 'PF') then 
	select	cd_pessoa_fisica 
	into STRICT	ds_retorno_w 
	from	agenda_paciente 
	where	nr_sequencia	=	nr_seq_agenda_p;
elsif (ie_opacao_p = 'CIR') then 
	select	nr_cirurgia 
	into STRICT	ds_retorno_w 
	from	agenda_paciente 
	where	nr_sequencia	=	nr_seq_agenda_p;
elsif (ie_opacao_p = 'AT') then 
	select	nr_atendimento 
	into STRICT	ds_retorno_w 
	from	agenda_paciente 
	where	nr_sequencia	=	nr_seq_agenda_p;
elsif (ie_opacao_p = 'PR') then 
	select	substr(obter_descricao_procedimento(cd_procedimento, ie_origem_proced),1,255) 
	into STRICT	ds_retorno_w 
	from	agenda_paciente 
	where	nr_sequencia	=	nr_seq_agenda_p;
elsif (ie_opacao_p = 'ME') then 
	select	substr(obter_nome_pf(cd_medico),1,255) 
	into STRICT	ds_retorno_w 
	from	agenda_paciente 
	where	nr_sequencia	=	nr_seq_agenda_p;
elsif (ie_opacao_p = 'DT') then 
	select	to_char(dt_agenda,'dd/mm/yyyy ') || to_char(hr_inicio,'hh24:mi:ss') 
	into STRICT	ds_retorno_w 
	from	agenda_paciente 
	where	nr_sequencia	=	nr_seq_agenda_p;
elsif (ie_opacao_p = 'PF') then 
	select	cd_pessoa_fisica 
	into STRICT	ds_retorno_w 
	from	agenda_paciente 
	where	nr_sequencia	=	nr_seq_agenda_p;
elsif (ie_opacao_p = 'PFM') then 
	select	cd_medico 
	into STRICT	ds_retorno_w 
	from	agenda_paciente 
	where	nr_sequencia	=	nr_seq_agenda_p;
elsif (ie_opacao_p = 'CID') then 
	select	obter_desc_cid(cd_doenca_cid) 
	into STRICT	ds_retorno_w 
	from	agenda_paciente 
	where	nr_sequencia	=	nr_seq_agenda_p;
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
