CREATE OR REPLACE FUNCTION obter_dados_agenda_cirurgica ( nr_seq_agenda_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
EA - Estabelecimento da agenda
CV - Convenio
NC - Nome do contato
CAT - NR_SEQ_CLASSIF_ATEND
*/
cd_estab_agenda_w	smallint;
ds_retorno_w		varchar(255);
cd_convenio_w		integer;
nm_paciente_contato_w	varchar(50);


BEGIN

if (coalesce(nr_seq_agenda_p,0) > 0) then
	select	max(a.cd_estabelecimento),
		max(b.cd_convenio)
	into STRICT	cd_estab_agenda_w,
		cd_convenio_w
	from	agenda a,
		agenda_paciente b
	where	a.cd_agenda 	= b.cd_agenda
	and	b.nr_sequencia	= nr_seq_agenda_p;	
	
	if (ie_opcao_p = 'EA') then
		ds_retorno_w	:= cd_estab_agenda_w;
	elsif (ie_opcao_p = 'CV') then	
		ds_retorno_w	:= cd_convenio_w;
	elsif (ie_opcao_p = 'NC') then	
		ds_retorno_w	:= nm_paciente_contato_w;
	elsif (ie_opcao_p = 'CAT') then
		begin
		select 	max(y.ds_classificacao)
		into STRICT	ds_retorno_w
      		from    classificacao_atendimento y,
                	agenda_paciente_auxiliar x
        	where   y.nr_sequencia = x.nr_seq_classif_atend
        	and     x.nr_seq_agenda = nr_seq_agenda_p;
		end;
	end if;
end if;	

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_agenda_cirurgica ( nr_seq_agenda_p bigint, ie_opcao_p text) FROM PUBLIC;

