CREATE OR REPLACE FUNCTION obter_dados_agenda_cirur ( nr_cirurgia_p bigint, ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE


/*

ie_opcao_p
P - Procedimento
N - Sequencia
C - Nome do contato
*/
ds_procedimento_w		PROCEDIMENTO.DS_PROCEDIMENTO%type;
ds_retorno_w			varchar(255);
nr_sequencia_w			bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
nm_pessoa_contato_w		varchar(50);


BEGIN

begin
select	cd_procedimento,
		ie_origem_proced,
		nr_sequencia,
		nm_pessoa_contato
into STRICT	cd_procedimento_w,
		ie_origem_proced_w,
		nr_sequencia_w,
		nm_pessoa_contato_w
from	agenda_paciente
where	nr_cirurgia = nr_cirurgia_p;
exception
	when others then
		nr_sequencia_w	:= null;
end;

if (ie_opcao_p = 'P') then
	ds_retorno_w	:= substr(obter_descricao_procedimento(cd_procedimento_w, ie_origem_proced_w),1,240);
elsif (ie_opcao_p = 'N') then
	ds_retorno_w	:= nr_sequencia_w;
elsif (ie_opcao_p = 'C') then
	ds_retorno_w	:= nm_pessoa_contato_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
