CREATE OR REPLACE FUNCTION obter_dados_agenda_jejum (nr_prescricao_p bigint, ie_inicio_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255);
ds_proc_cirurgia_w	varchar(255);
cd_pessoa_fisica_w	varchar(30);
cd_tipo_agenda_w	integer;
dt_hr_inicio_w		timestamp;
dt_inicio_prescr_w	timestamp;
dt_validade_prescr_w	timestamp;

/*	ie_opcao_p 
D	- Dt inicio agenda 
P	- Procedimento 
*/
 
 
C01 CURSOR FOR 
SELECT	a.hr_inicio, 
	a.ds_proc_cirurgia 
from	agenda_paciente_v a 
where	clock_timestamp()			< a.hr_inicio 
and	a.cd_tipo_agenda	= cd_tipo_agenda_w 
and	a.ie_status_agenda	not in ('L','I','C','B','F') 
and	a.cd_pessoa_fisica 	= cd_pessoa_fisica_w 
and	a.hr_inicio between dt_inicio_prescr_w and dt_validade_prescr_w 
order by a.hr_inicio desc;


BEGIN 
 
select	max(a.cd_pessoa_fisica), 
	max(a.dt_inicio_prescr), 
	max(a.dt_validade_prescr) 
into STRICT	cd_pessoa_fisica_w, 
	dt_inicio_prescr_w, 
	dt_validade_prescr_w 
from	prescr_medica a 
where	nr_prescricao	= nr_prescricao_p;
 
if (ie_inicio_p	= 'C') then 
	cd_tipo_agenda_w	:= 1;
elsif (ie_inicio_p	= 'P') then 
	cd_tipo_agenda_w	:= 2;
end if;
 
open c01;
loop 
fetch c01 into 
	dt_hr_inicio_w, 
	ds_proc_cirurgia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	dt_hr_inicio_w		:= dt_hr_inicio_w;
	ds_proc_cirurgia_w	:= ds_proc_cirurgia_w;
end loop;
close c01;
 
if (ie_opcao_p	= 'D') then 
	ds_retorno_w	:= to_char(dt_hr_inicio_w,'dd/mm/yyyy hh24:mi:ss');
elsif (ie_opcao_p	= 'P') then 
	ds_retorno_w	:= ds_proc_cirurgia_w;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_agenda_jejum (nr_prescricao_p bigint, ie_inicio_p text, ie_opcao_p text) FROM PUBLIC;

