CREATE OR REPLACE FUNCTION obter_dados_agenda_servico (nr_seq_agenda_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
options:
QS	- qtde de sessions
S	- sessions atual;
QSS	- qtde de sessions / sessions atual 
QPA	- qtde de proced adicional
*/
qt_sessao_w	smallint;
nr_sessao_w	smallint;
ds_retorno_w	varchar(255)	:= '';


BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	select	coalesce(max(qt_total_secao),0),
		coalesce(max(nr_secao),0)
	into STRICT	qt_sessao_w,
		nr_sessao_w
	from	agenda_consulta
	where	nr_sequencia = nr_seq_agenda_p;

	if (coalesce(ie_opcao_p,'QSS') = 'QSS') and (qt_sessao_w IS NOT NULL AND qt_sessao_w::text <> '') and (nr_sessao_w IS NOT NULL AND nr_sessao_w::text <> '') then
		ds_retorno_w	:= nr_sessao_w || ' / ' || qt_sessao_w;
	elsif (coalesce(ie_opcao_p,'QSS') = 'QPA') then
		select	coalesce(count(*),0)
		into STRICT	ds_retorno_w
		from	agenda_consulta_proc
		where	nr_seq_agenda = nr_seq_agenda_p;
	elsif (coalesce(ie_opcao_p,'QSS') = 'S') then
		ds_retorno_w	:= nr_sessao_w;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
