CREATE OR REPLACE FUNCTION obter_dados_agenda_solic_san (nr_seq_agenda_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
T - tipagem sangu√≠nea
B - quantidade de bolsas
*/
ds_retorno_w		varchar(80);
IE_TIPAGEM_SANGUINEA_w	varchar(1);
QT_BOLSAS_SANGUE_w	smallint;


BEGIN

select 	max(IE_TIPAGEM_SANGUINEA),
	max(QT_BOLSAS_SANGUE)
into STRICT	IE_TIPAGEM_SANGUINEA_w,
	QT_BOLSAS_SANGUE_w
from	agenda_pac_sangue
where 	nr_seq_agenda = nr_seq_agenda_p;

if (ie_opcao_p = 'T') then
	ds_retorno_w	:= IE_TIPAGEM_SANGUINEA_w;
elsif (ie_opcao_p = 'B') then
	ds_retorno_w	:= QT_BOLSAS_SANGUE_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
