CREATE OR REPLACE FUNCTION obter_dados_agenda ( cd_pessoa_fisica_p text, dt_agenda_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* 
C - Convênio 
L - Classificação 
*/
 
 
dt_agenda_ultima_w		timestamp;
ds_convenio_w			varchar(255);
ds_classif_agenda_w		varchar(60);
ds_retorno_w			varchar(60);

 
c01 CURSOR FOR 
	SELECT	substr(ds_convenio,1,60), 
		substr(ds_classif_agenda_pac,1,60), 
		dt_agenda 
	from	agenda_paciente_v 
	where	cd_tipo_agenda IN (1,2) 
	and	cd_pessoa_fisica	= cd_pessoa_fisica_p 
	and	trunc(dt_agenda)	= dt_agenda_p 
	and	(nr_seq_classif_agenda IS NOT NULL AND nr_seq_classif_agenda::text <> '') 
	and	(dt_agenda IS NOT NULL AND dt_agenda::text <> '') 
	and 	(cd_convenio IS NOT NULL AND cd_convenio::text <> '') 
	
union
 
	SELECT	substr(ds_convenio,1,60), 
		substr(ds_classif_agenda,1,60), 
		dt_agenda 
	from	agenda_consulta_v2 
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p 
	and	trunc(dt_agenda)	= dt_agenda_p 
	and	(ie_classif_agenda IS NOT NULL AND ie_classif_agenda::text <> '') 
	and	(dt_agenda IS NOT NULL AND dt_agenda::text <> '') 
	and	(cd_convenio IS NOT NULL AND cd_convenio::text <> '') 
	order by dt_agenda;


BEGIN 
 
open c01;
	loop 
		fetch c01 into	 
			ds_convenio_w, 
			ds_classif_agenda_w, 
			dt_agenda_ultima_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */	
			begin 
			ds_convenio_w		:= ds_convenio_w;
			end;		
	end loop;
close c01;
 
if (ie_opcao_p = 'C') then 
	ds_retorno_w := ds_convenio_w;
elsif (ie_opcao_p = 'L') then 
	ds_retorno_w := ds_classif_agenda_w;
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_agenda ( cd_pessoa_fisica_p text, dt_agenda_p text, ie_opcao_p text) FROM PUBLIC;

