SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_agend_coletivo ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p:
AG	Pacientes com atendimento gerado
AI	SequÃªncia da agenda
TR	Paciente triado (Item acolhimento do PEP)
*/
nr_seq_agenda_int_w	bigint;
ds_retorno_w	varchar(255);


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	if (ie_opcao_p = 'AI') then
		select	max(a.nr_seq_agenda_int)
		into STRICT	ds_retorno_w
		from	agendamento_coletivo a
		where	a.nr_sequencia = (	SELECT	max(b.nr_seq_agend_coletiva)
					from	agenda_consulta b
					where	b.nr_atendimento = nr_atendimento_p);
	elsif (ie_opcao_p = 'AG') then
		select	a.nr_seq_agenda_int
		into STRICT	nr_seq_agenda_int_w
		from	agendamento_coletivo a
		where	a.nr_sequencia = (	SELECT	max(b.nr_seq_agend_coletiva)
					from	agenda_consulta b
					where	b.nr_atendimento = nr_atendimento_p);

		select	coalesce(max('N'),'S')
		into STRICT	ds_retorno_w
		from	agenda_consulta a
		where	a.nr_seq_agend_coletiva in (	SELECT	b.nr_sequencia
						from	agendamento_coletivo b
						where	b.nr_seq_agenda_int = nr_seq_agenda_int_w)
		and	coalesce(a.nr_atendimento::text, '') = '';
	elsif (ie_opcao_p = 'TR') then
		select	coalesce(max('S'),'N')
		into STRICT	ds_retorno_w
		from	atend_escuta_inicial a
		where	a.nr_atendimento = nr_atendimento_p
		and	a.ie_situacao = 'A'
		and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
