SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_agente ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_modo_adm_w		varchar(15);
ds_retorno_w		varchar(255);
ie_tipo_dosagem_w	varchar(15);
cd_unidade_medida_w	varchar(30);
qt_ml_vapor_w		double precision;


BEGIN

if (nr_sequencia_p > 0) then
	select	ie_modo_adm,
		ie_tipo_dosagem,
		cd_unidade_medida,
		coalesce(qt_ml_vapor,0)
	into STRICT	ie_modo_adm_w,
		ie_tipo_dosagem_w,
		cd_unidade_medida_w,
		qt_ml_vapor_w
	from	agente_anestesico
	where	nr_sequencia	=	nr_sequencia_p;
end if;

if (ie_opcao_p = 'MA') then
	ds_retorno_w	:= ie_modo_adm_w;
elsif (ie_opcao_p = 'DTD') then
	ds_retorno_w	:= obter_valor_dominio(1580,ie_tipo_dosagem_w);
elsif (ie_opcao_p = 'UM') then
	ds_retorno_w	:= substr(obter_desc_unid_med(cd_unidade_medida_w),1,100);
elsif (ie_opcao_p = 'QVML') then
	ds_retorno_w	:= qt_ml_vapor_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
