CREATE OR REPLACE FUNCTION obter_dados_agepac_colunas ( cd_agenda_p bigint, nm_usuario_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_Retorno_w	bigint;
nr_height_w	bigint;
nr_width_w	bigint;


BEGIN

select	coalesce(max(nr_height),0),
	coalesce(max(nr_width),0)
into STRICT	nr_height_w,
	nr_width_w
from	agepac_colunas_temp
where	nm_usuario_coluna	= nm_usuario_p
and	cd_agenda		= cd_agenda_p;

if (ie_opcao_p	= 'W') then
	vl_Retorno_w	:= nr_width_w;
else
	vl_Retorno_w	:= nr_height_w;
end if;

return	vl_Retorno_w;

end;
$body$
