SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_age_cons (nr_seq_agenda_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(10);

-- Opção: 	C - Cd_agenda
--	S - Se existe vínculo
BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (ie_opcao_p = 'C') then

	select 	max(to_char(cd_agenda))
	into STRICT	ds_retorno_w
	from	agenda_consulta
	where	nr_seq_agepaci	=	nr_seq_agenda_p;
end if;

if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (ie_opcao_p = 'S') then

	select  coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	agenda_consulta
	where	nr_seq_agepaci = nr_seq_agenda_p;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
