CREATE OR REPLACE FUNCTION obter_dados_ajuste_estoque ( dt_mesano_referencia_p timestamp, cd_centro_custo_p bigint, cd_local_estoque_p bigint, cd_material_p bigint, cd_estabelecimento_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*	opção
	V = Valor
	Q = quantidade */
vl_ajuste_w		double precision;
qt_ajuste_w		double precision;
ds_retorno_w		double precision;


BEGIN


select	sum(CASE WHEN b.ie_forma_ajuste='D' THEN b.vl_ajuste * -1 WHEN b.ie_forma_ajuste='A' THEN b.vl_ajuste  ELSE 0 END ),
	sum(CASE WHEN b.ie_forma_ajuste='D' THEN b.qt_ajuste * -1 WHEN b.ie_forma_ajuste='A' THEN b.qt_ajuste  ELSE 0 END )
into STRICT	vl_ajuste_w,
	qt_ajuste_w
from	sup_ajuste_estoque_consumo a,
	sup_ajuste_est_cons_mat b
where	a.nr_sequencia = b.nr_seq_ajuste
and	a.cd_estabelecimento = cd_estabelecimento_p
and	a.dt_mesano_referencia = dt_mesano_referencia_p
and (a.cd_centro_custo = cd_centro_custo_p or coalesce(cd_centro_custo_p::text, '') = '')
and (a.cd_local_estoque = cd_local_estoque_p or coalesce(cd_local_estoque_p::text, '') = '')
and	b.cd_material = cd_material_p
and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');

if (ie_opcao_p = 'V') then
	ds_retorno_w := vl_ajuste_w;
elsif (ie_opcao_p = 'Q') then
	ds_retorno_w := qt_ajuste_w;
end if;

return	coalesce(ds_retorno_w,0);

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
