SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_alta_transf ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
	DM - Descrição do motivo
	O  - Observação
	HD - Hospital destino
*/
cd_cgc_w		atendimento_transf.cd_cgc%type;
nr_seq_motivo_w		atendimento_transf.nr_seq_motivo%type;
ds_observacao_w		atendimento_transf.ds_observacao%type;
ds_motivo_transf_w	varchar(60);
ds_pessoa_juridica_w	varchar(100);
ds_retorno_w		atendimento_transf.ds_observacao%type;

C01 CURSOR FOR
	SELECT	cd_cgc,
		substr(obter_nome_pf_pj(null,cd_cgc),1,100),
		nr_seq_motivo,
		substr(obter_desc_motivo_transf_pac(nr_seq_motivo),1,60),
		ds_observacao
	from	atendimento_transf
	where	nr_atendimento	= nr_atendimento_p
	order by nr_sequencia;

BEGIN

open C01;
loop
	fetch C01 into
		cd_cgc_w,
		ds_pessoa_juridica_w,
		nr_seq_motivo_w,
		ds_motivo_transf_w,
		ds_observacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	cd_cgc_w		:= cd_cgc_w;
	ds_pessoa_juridica_w	:= ds_pessoa_juridica_w;
	nr_seq_motivo_w		:= nr_seq_motivo_w;
	ds_motivo_transf_w	:= ds_motivo_transf_w;
	ds_observacao_w		:= ds_observacao_w;
	end;
end loop;
close C01;

if (ie_opcao_p = 'DM') then
	ds_retorno_w	:= ds_motivo_transf_w;
elsif (ie_opcao_p = 'O') then
	ds_retorno_w	:= ds_observacao_w;
elsif (ie_opcao_p = 'HD') then
	ds_retorno_w	:= ds_pessoa_juridica_w;
end if;


return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
