CREATE OR REPLACE FUNCTION obter_dados_alter_con_cons ( cd_evento_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/* ie_opcao
PR -> Percentual
NR -> SequÃªncia
*/
nr_retorno_w		varchar(255);
pr_conhecimento_w	smallint;
nr_sequencia_w		bigint;


BEGIN
select	coalesce(max(pr_conhecimento),0),
		coalesce(max(nr_sequencia),0)
into STRICT	pr_conhecimento_w,
		nr_sequencia_w
from	proj_conhec_padrao
where	cd_evento = cd_evento_p;

if (ie_opcao_p = 'PR') then
		nr_retorno_w := pr_conhecimento_w;
elsif (ie_opcao_p = 'NR') then
		nr_retorno_w := nr_sequencia_w;
end if;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
