CREATE OR REPLACE FUNCTION obter_dados_antecip ( nr_atendimento_p ATEND_CATEGORIA_CONVENIO.NR_ATENDIMENTO%type, nr_transacao_finan_p TRANSACAO_FINANCEIRA.NR_SEQUENCIA%type, ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w  ATEND_CATEGORIA_CONVENIO.DS_TARJA_CARTAO%type;
ie_cota_moderadora_w TRANSACAO_FINANCEIRA.IE_COTA_MODERADORA%type;


BEGIN

SELECT  MAX(IE_COTA_MODERADORA)
INTO STRICT    ie_cota_moderadora_w
FROM    TRANSACAO_FINANCEIRA
WHERE   NR_SEQUENCIA = nr_transacao_finan_p;

IF ie_cota_moderadora_w = 'S' THEN

SELECT  MAX(CASE
          WHEN ie_opcao_p = 'DSP' THEN OBTER_DESC_PLANO_CONV(a.CD_CONVENIO, a.CD_PLANO_CONVENIO)
          WHEN ie_opcao_p = 'SE'  THEN a.CD_SENHA
          WHEN ie_opcao_p = 'CDN' THEN TO_CHAR(a.NR_SEQ_NIVEL_FILIACAO)
          WHEN ie_opcao_p = 'DSN' THEN OBTER_DADOS_REGIME_FILIACAO(a.NR_SEQ_NIVEL_FILIACAO, 'VCM')
          WHEN ie_opcao_p = 'VLC' THEN TO_CHAR(b.VL_COTA_MODERADORA)
        END)
INTO STRICT    ds_retorno_w
FROM    ATEND_CATEGORIA_CONVENIO a,
        NIVEL_FILIACAO b
WHERE   a.NR_SEQ_NIVEL_FILIACAO = b.NR_SEQUENCIA
AND     a.NR_ATENDIMENTO        = nr_atendimento_p;

END IF;

RETURN  ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
