CREATE OR REPLACE FUNCTION obter_dados_apache_iv ( ie_opcao_p text, nr_seq_apache_iv_p bigint) RETURNS varchar AS $body$
DECLARE


/*
	DMC = Disease Maping Code
*/
cd_diag_map_cod_w	varchar(20);


BEGIN

if (ie_opcao_p = 'DMC') then /* Disease Maping Code */
	select	b.cd_mapeamento
	into STRICT	cd_diag_map_cod_w
	from 	diag_doenca_apache_iv a,
			map_doenca_apache_iv b
	where	a.nr_seq_mapeamento = b.nr_sequencia
	and		a.nr_sequencia = nr_seq_apache_iv_p;
end if;

return cd_diag_map_cod_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
