CREATE OR REPLACE FUNCTION obter_dados_area_curriculo ( nr_seq_area_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_area_w	varchar(255);
ds_retorno_w	varchar(255);


BEGIN

select	ds_area
into STRICT	ds_area_w
from	area_curriculo
where	nr_sequencia	= nr_seq_area_p;

if (ie_opcao_p	= 'D') then
	ds_retorno_w	:= ds_area_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
