CREATE OR REPLACE FUNCTION obter_dados_arquivo_envio ( ds_arquivo_envio_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* 
'C'	-- Código do convênio 
'D'	-- Nome do convênio 
*/
 
 
nr_seq_arquivo_w	varchar(15);
cd_convenio_w		bigint;
ds_convenio_w		varchar(255);


BEGIN 
 
select	substr(ds_arquivo_envio_p, 15, 4) 
into STRICT	nr_seq_arquivo_w
;
 
select	max(cd_convenio), 
	max(obter_nome_convenio(cd_convenio)) 
into STRICT	cd_convenio_w, 
	ds_convenio_w 
from	protocolo_convenio 
where	lpad(nr_seq_envio_convenio,4,0)	= nr_seq_arquivo_w;
 
if (ie_opcao_p	= 'C') then 
	return cd_convenio_w;
else 
	return ds_convenio_w;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
