SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_atendimento_rn ( nr_atendimento_mae_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

						 
ds_retorno_w		bigint;
nr_atendimento_w	bigint;

BEGIN
 
if (coalesce(nr_atendimento_mae_p,0) > 0) then 
 
	if (lower(ie_opcao_p) = 'atend') then 
		begin 
		 
		select 	max(nr_atendimento) 
		into STRICT	ds_retorno_w 
		from	atendimento_paciente 
		where	nr_atendimento_mae = nr_atendimento_mae_p;		
		exception 
		when others then 
			ds_retorno_w := '';
		end;
 
	elsif (lower(ie_opcao_p) = 'nome') then 
		 
		begin 
		 
		select 	obter_pessoa_atendimento(nr_atendimento,'N') 
		into STRICT	ds_retorno_w 
		from	atendimento_paciente 
		where	nr_atendimento_mae = nr_atendimento_mae_p;		
		exception 
		when others then 
			ds_retorno_w := '';
		end;
		 
	elsif (lower(ie_opcao_p) = 'unid') then 
 
		begin 
		 
		select 	max(nr_atendimento) 
		into STRICT	nr_atendimento_w 
		from	atendimento_paciente 
		where	nr_atendimento_mae = nr_atendimento_mae_p;
		 
		select	substr(e.cd_unidade_basica||' '||e.cd_unidade_compl,1,255) 
		into STRICT	ds_retorno_w 
		from	setor_atendimento a, 
			atend_paciente_unidade c, 
			unidade_atendimento e 
		where	e.cd_setor_atendimento	= c.cd_setor_atendimento 
		and	e.cd_unidade_basica	= c.cd_unidade_basica 
		and	e.cd_unidade_compl	= c.cd_unidade_compl 
		and	a.cd_setor_atendimento	= e.cd_setor_atendimento 
		and	a.cd_estabelecimento	= obter_estabelecimento_ativo 
		and	c.nr_seq_interno	= 
			(	SELECT	coalesce(max(x.nr_seq_interno),0) 
				from	atend_paciente_unidade x 
				where	x.nr_atendimento = nr_atendimento_w 
				and 	coalesce(x.dt_saida_unidade, x.dt_entrada_unidade + 9999)	= 
						(	select	max(coalesce(t.dt_saida_unidade, t.dt_entrada_unidade + 9999)) 
							from	Setor_Atendimento y, 
								atend_paciente_unidade t 
							where 	nr_atendimento 	= nr_atendimento_w 
							and	t.cd_setor_atendimento	= y.cd_setor_atendimento 
							and	y.cd_classif_setor in (9)))  LIMIT 1;
		exception 
		when others then 
				ds_retorno_w := '';
		end;
		 
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_atendimento_rn ( nr_atendimento_mae_p bigint, ie_opcao_p text) FROM PUBLIC;

