SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_atend_futuro ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_status_w	varchar(255);
ds_seq_status_w	varchar(100);
ds_retorno_w	varchar(255);


BEGIN

if (nr_sequencia_p > 0) then
	select	substr(obter_desc_status_atend_fut(nr_seq_status),1,255),
		substr(nr_seq_status,1,100)
	into STRICT	ds_status_w,
		ds_seq_status_w
	from	atend_pac_futuro
	where	nr_sequencia	=	nr_sequencia_p;

	if (ie_opcao_p = 'DS') then
		ds_retorno_w	:=	ds_status_w;
	elsif (ie_opcao_p = 'C') then
		ds_retorno_w	:=	ds_seq_status_w;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
