SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_atepacu ( nr_seq_interno_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE



cd_setor_atendimento_w		integer;
ds_retorno_w			varchar(100);	
nr_seq_classif_esp_w		atendimento_paciente.nr_seq_classif_esp%type;
cd_departamento_w		departamento_medico.cd_departamento%type;

c01 CURSOR FOR
SELECT	nr_seq_classif_esp
from	atend_pac_unid_classif_esp
where	nr_seq_atend_pac_unidade = nr_seq_interno_p
order by	dt_inicial desc;

c01_w	c01%rowtype;


BEGIN

if (coalesce(nr_seq_interno_p,0) <> 0) then
	select	max(cd_setor_atendimento)
	into STRICT	cd_setor_atendimento_w
	from	atend_paciente_unidade
	where	nr_seq_interno = nr_seq_interno_p;

	if (ie_opcao_p = 0) then
		ds_retorno_w := cd_setor_atendimento_w;
	elsif (ie_opcao_p = 1) then
		select	max(ds_setor_atendimento)
		into STRICT	ds_retorno_w
		from	setor_atendimento
		where	cd_setor_atendimento = cd_setor_atendimento_w;
	elsif (ie_opcao_p = 2) then
		select	substr(max(ds_setor_atendimento),1,25) || ' ' || max(a.cd_unidade_basica) || ' ' || max(a.cd_unidade_compl)
		into STRICT	ds_retorno_w
		from	atend_paciente_unidade a,
			setor_atendimento b
		where	a.cd_setor_atendimento	= b.cd_setor_atendimento
		and	a.nr_seq_interno 	= nr_seq_interno_p;
	elsif (ie_opcao_p = 3) then
		begin
		select	nr_seq_classif_esp
		into STRICT	nr_seq_classif_esp_w
		from	atend_paciente_unidade a
		where	a.nr_seq_interno = nr_seq_interno_p
		and	(nr_seq_classif_esp IS NOT NULL AND nr_seq_classif_esp::text <> '');
		
		ds_retorno_w	:=	nr_seq_classif_esp_w;
		exception
		when others then
			begin
			open c01;
			loop
			fetch c01 into	
				c01_w;
			exit when(c01%notfound or (c01_w.nr_seq_classif_esp IS NOT NULL AND c01_w.nr_seq_classif_esp::text <> ''));			
			end loop;
			close c01;
			
			ds_retorno_w	:=	c01_w.nr_seq_classif_esp;
			end;
		end;
	elsif (ie_opcao_p in (4,41)) then
		begin

		select	coalesce(max(a.cd_departamento),0),
			max(substr(b.ds_departamento,1,100))
		into STRICT	cd_departamento_w,
			ds_retorno_w
		from	departamento_medico b,
			atend_paciente_unidade a
		where	a.cd_departamento = b.cd_departamento
		and	a.nr_seq_interno = nr_seq_interno_p;

		if (cd_departamento_w = 0) then
			ds_retorno_w	:= null;
		elsif (ie_opcao_p = 4) then
			ds_retorno_w	:= cd_departamento_w;
		end if;

		end;
	end if;
end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
