CREATE OR REPLACE FUNCTION obter_dados_atributo ( nm_atributo_p text, nm_tabela_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p 

Ja criei a procedure com o ie_opcao_p para ser utilizado futuramente com outras opcoes

"LB" -> Label
"LBG" -> Quando nao ha Label busca a Label do Grid	wcbernardino OS 364203

*/
ds_label_w	varchar(255);


BEGIN

select	substr(obter_desc_expressao(cd_exp_label,null), 1,255)
into STRICT	ds_label_w
from	tabela_atributo
where	nm_tabela	= nm_tabela_p
and	nm_atributo	= nm_atributo_p;

if (ie_opcao_p = 'LBG') then
	select	coalesce(trim(both substr(obter_desc_expressao(cd_exp_label,null), 1,255)),substr(obter_desc_expressao(cd_exp_label_grid,null), 1,255))
	into STRICT	ds_label_w
	from	tabela_atributo
	where	nm_tabela	= nm_tabela_p
	and	nm_atributo	= nm_atributo_p;
end if;

return	ds_label_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
