SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_audit_conpaci ( nr_interno_conta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_empresa_w	auditoria_conta_paciente.nr_seq_empresa%type;
nr_rat_w		auditoria_conta_paciente.nr_rat%type;
dt_rat_w		varchar(8);

BEGIN

begin
select	a.nr_seq_empresa,
	a.nr_rat,
	to_char(a.dt_inclusao_rat,'ddmmyyyy')
into STRICT	nr_seq_empresa_w,
	nr_rat_w,
	dt_rat_w
from	auditoria_conta_paciente a
where	a.nr_interno_conta = nr_interno_conta_p
and	a.nr_sequencia	   = (	SELECT 	max(x.nr_sequencia)
				from	auditoria_conta_paciente x
				where	x.nr_interno_conta = a.nr_interno_conta);
exception
when others then
	nr_seq_empresa_w := null;
	nr_rat_w	 := null;
	dt_rat_w	 := null;
end;


if (ie_opcao_p = 'E') and (nr_seq_empresa_w IS NOT NULL AND nr_seq_empresa_w::text <> '') then

	select	coalesce(max(cd_externo),0)
	into STRICT	ds_retorno_w
	from	empresa_auditoria
	where	nr_sequencia = nr_seq_empresa_w;


elsif (ie_opcao_p = 'R') and (nr_rat_w IS NOT NULL AND nr_rat_w::text <> '') then
	ds_retorno_w := nr_rat_w;
elsif (ie_opcao_p = 'D') then
	ds_retorno_w := dt_rat_w;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
