SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_autor_agenda (nr_seq_agenda_p bigint, nr_seq_agenda_cons_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
s - senha
IV - Inicio vigencia
FV - Fim vigencia
*/
cd_senha_w		varchar(20);
ds_retorno_w		varchar(100);
dt_inicio_vigencia_w	varchar(30);
dt_fim_vigencia_w	varchar(30);

c01 CURSOR FOR
SELECT	cd_senha,
	to_char(dt_inicio_vigencia,'dd/mm/yyyy hh24:mi:ss'),
	to_char(dt_fim_vigencia,'dd/mm/yyyy hh24:mi:ss')	
from	autorizacao_convenio
where	nr_seq_agenda	= nr_seq_agenda_p
and	(cd_senha IS NOT NULL AND cd_senha::text <> '')
order by dt_autorizacao;

c02 CURSOR FOR
SELECT	cd_senha
from	autorizacao_convenio
where	nr_seq_agenda	= nr_seq_agenda_cons_p
and	(cd_senha IS NOT NULL AND cd_senha::text <> '');


BEGIN

if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	open	c01;
	loop
	fetch	c01 into
		cd_senha_w,
		dt_inicio_vigencia_w,
		dt_fim_vigencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		if (ie_opcao_p	= 'S') then
			ds_retorno_w	:= cd_senha_w;
		end if;
		
		if (ie_opcao_p	= 'IV') then
			ds_retorno_w	:= dt_inicio_vigencia_w;
		end if;
		
		if (ie_opcao_p	= 'FV') then
			ds_retorno_w	:= dt_fim_vigencia_w;
		end if;
	end loop;
	close c01;
elsif (nr_seq_agenda_cons_p IS NOT NULL AND nr_seq_agenda_cons_p::text <> '') then
	open	c02;
	loop
	fetch	c02 into
		cd_senha_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		if (ie_opcao_p	= 'S') then
			ds_retorno_w	:= cd_senha_w;
		end if;
	end loop;
	close c02;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_autor_agenda (nr_seq_agenda_p bigint, nr_seq_agenda_cons_p bigint, ie_opcao_p text) FROM PUBLIC;

