CREATE OR REPLACE FUNCTION obter_dados_autor_cot_compra (nr_cot_compra_p bigint, nr_item_cot_compra_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
C - Codigo pessoa fisica (Paciente)
N - Nome pessoa fisica (Paciente)
*/
ds_retorno_w		varchar(255);
nr_seq_agenda_w		agenda_paciente.nr_sequencia%type;
nr_atendimento_w	bigint;
cd_pessoa_fisica_w	varchar(10);
nm_pessoa_fisica_w	varchar(255);


BEGIN

ds_retorno_w	:= null;

select	max(a.nr_seq_agenda),
	max(a.nr_atendimento),
	max(a.cd_pessoa_fisica)
into STRICT	nr_seq_agenda_w,
	nr_atendimento_w,
	cd_pessoa_fisica_w
from	autorizacao_cirurgia a,
	material_autor_cirurgia b
where	a.nr_sequencia		= b.nr_seq_autorizacao
and	b.nr_cot_compra 	= nr_cot_compra_p
and	b.nr_item_cot_compra	= nr_item_cot_compra_p;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then

	select	max(substr(obter_nome_pf_pj(cd_pessoa_fisica_w,null),1,255))
	into STRICT	nm_pessoa_fisica_w
	;
	
elsif (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then

	select	max(cd_pessoa_fisica),
		max(substr(obter_nome_pf_pj(cd_pessoa_fisica,null),1,255))
	into STRICT	cd_pessoa_fisica_w,
		nm_pessoa_fisica_w
	from	atendimento_paciente
	where	nr_atendimento	= nr_atendimento_w;
	
elsif (nr_seq_agenda_w IS NOT NULL AND nr_seq_agenda_w::text <> '') then

	select	max(cd_pessoa_fisica),
		max(substr(obter_nome_pf_pj(cd_pessoa_fisica,null),1,255))
	into STRICT	cd_pessoa_fisica_w,
		nm_pessoa_fisica_w
	from	agenda_paciente
	where	nr_sequencia	= nr_seq_agenda_w;
	
end if;

if (ie_opcao_p = 'C') then
	ds_retorno_w	:= cd_pessoa_fisica_w;
elsif (ie_opcao_p = 'N') then
	ds_retorno_w	:= nm_pessoa_fisica_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_autor_cot_compra (nr_cot_compra_p bigint, nr_item_cot_compra_p bigint, ie_opcao_p text) FROM PUBLIC;

