CREATE OR REPLACE FUNCTION obter_dados_autor_radio (nr_seq_rxt_tratamento_p bigint, ie_cod_descricao_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_estagio_w	estagio_autorizacao.nr_sequencia%type;
/*
ie_opcao_p

E - Estágio da autorização da radioterapia

=======================================================
ie_cod_descricao_p

C - código
D - Descrição
*/
BEGIN
select	max(nr_seq_estagio)
into STRICT	nr_seq_estagio_w
from	autorizacao_convenio
where	nr_seq_rxt_tratamento = nr_seq_rxt_tratamento_p;


if (ie_opcao_p = 'E') and (nr_seq_estagio_w IS NOT NULL AND nr_seq_estagio_w::text <> '') then

	if (ie_cod_descricao_p = 'D') then

		select	substr(ds_estagio,1,250)
		into STRICT	ds_retorno_w
		from	estagio_autorizacao
		where	nr_sequencia = nr_seq_estagio_w
		and	OBTER_EMPRESA_ESTAB(wheb_usuario_pck.get_cd_estabelecimento) = cd_empresa;
	else
		ds_retorno_w := nr_seq_estagio_w;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
