CREATE OR REPLACE FUNCTION obter_dados_auto_regra_agenda (cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE



ie_carater_cirurgia_w	varchar(2) := null;
qt_registros_w		bigint;
cd_grupo_proc_w		bigint;
cd_especialidade_w	bigint;
cd_area_proc_w		bigint;



c01 CURSOR FOR
	SELECT	substr(ie_carater_cirurgia,1,2)
	from	regra_dados_aut_agenda_cir
	where	coalesce(cd_procedimento,coalesce(cd_procedimento_p,0))		 = coalesce(cd_procedimento_p,0)
	and	coalesce(ie_origem_proced,coalesce(ie_origem_proced_p,1))		 = coalesce(ie_origem_proced_p,1)
	and	coalesce(cd_convenio,coalesce(cd_convenio_p,0))		 = coalesce(cd_convenio_p,0)
	and	coalesce(cd_grupo_proc,coalesce(cd_grupo_proc_w,0))	 = coalesce(cd_grupo_proc_w,0)
	and	coalesce(cd_especialidade, coalesce(cd_especialidade_w,0)) = coalesce(cd_especialidade_w,0)
	and	coalesce(cd_area_procedimento, coalesce(cd_area_proc_w,0)) = coalesce(cd_area_proc_w,0)
	order by coalesce(cd_procedimento,0),
		coalesce(ie_origem_proced,coalesce(ie_origem_proced_p,0)),
		coalesce(cd_grupo_proc,0),
		coalesce(cd_especialidade,0),
		coalesce(cd_area_procedimento,0);


BEGIN

if (coalesce(cd_procedimento_p,0) > 0) and (coalesce(ie_origem_proced_p,0) > 0) then

	select count(*)
	into STRICT qt_registros_w
	from regra_dados_aut_agenda_cir;

	if (qt_registros_w > 0) then

		select cd_grupo_proc,
		       (obter_dados_estrut_proc(cd_procedimento,ie_origem_proced,'C','E'))::numeric ,
		       (obter_dados_estrut_proc(cd_procedimento,ie_origem_proced,'C','A'))::numeric
		into STRICT   cd_grupo_proc_w,
		       cd_especialidade_w,
		       cd_area_proc_w
		from   procedimento
		where  cd_procedimento  = cd_procedimento_p
		and    ie_origem_proced = ie_origem_proced_p;


		open C01;
		loop
		fetch C01 into
			ie_carater_cirurgia_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			ie_carater_cirurgia_w := ie_carater_cirurgia_w;
			end;
		end loop;
		close C01;
	end if;
end if;

return ie_carater_cirurgia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
