SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_aval_atendimento (ie_opcao_p text, nr_seq_tipo_avaliacao_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
/*        ie_opcao_p 
U	-	Último registro 
HUR	-	Horas do último registro 
*/
 
 
dt_alta_w   timestamp;
dt_entrada_w timestamp;
ds_retorno_w varchar(50);
dt_registro_w timestamp;


BEGIN 
 
SELECT	dt_alta, 
	dt_entrada 
INTO STRICT	dt_alta_w, 
	dt_entrada_w 
FROM	atendimento_paciente 
WHERE	nr_atendimento = nr_atendimento_p;
 
SELECT	MAX(dt_avaliacao) 
INTO STRICT	dt_registro_w 
FROM	med_avaliacao_paciente 
WHERE	nr_sequencia = (SELECT	coalesce(MAX(nr_sequencia),0) 
			FROM	med_avaliacao_paciente 
			WHERE	nr_atendimento	= nr_atendimento_p 
			AND	nr_seq_tipo_avaliacao = nr_seq_tipo_avaliacao_p 
			AND	coalesce(dt_inativacao::text, '') = '');
 
IF (ie_opcao_p = 'U') THEN 
	 ds_retorno_w	:= TO_CHAR(dt_registro_w,'dd/mm/yyyy hh24:mi:ss');
ELSIF (ie_opcao_p = 'HUR') THEN 
	 ds_retorno_w	:= TRUNC(((coalesce(dt_alta_w,clock_timestamp()) - coalesce(dt_registro_w,dt_entrada_w)) * 24),2);
END IF;
 
RETURN	ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
