SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_aval_nutric (nr_atendimento_p bigint, ie_informacao_p text) RETURNS varchar AS $body$
DECLARE

/*
ie_informacao_p
DN - Diagnóstico Nutricional
NC - Necessidade Calórica
NP - Necessidade Protéica
C - Caloria
P - Proteína
*/
ds_retorno_w		varchar(255);
nr_seq_aval_nut_w	bigint;


BEGIN

select	max(a.nr_sequencia)
into STRICT	nr_seq_aval_nut_w
from	aval_nutricao a
where	a.nr_atendimento = nr_atendimento_p
and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
and	a.dt_avaliacao = (	SELECT	max(b.dt_avaliacao)
				from	aval_nutricao b
				where	b.nr_atendimento = nr_atendimento_p
				and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> ''));

if (nr_seq_aval_nut_w IS NOT NULL AND nr_seq_aval_nut_w::text <> '') then

if (ie_informacao_p	= 'DN') then

	select	substr(Obter_Valor_Dominio(4844,max(ie_aval_nut)),1,255)
	into STRICT	ds_retorno_w
	from	aval_nutricao
	where	nr_sequencia	= nr_seq_aval_nut_w;

elsif (ie_informacao_p	= 'NC') then

	select	max(qt_nec_calorica)
	into STRICT	ds_retorno_w
	from	aval_nutricao
	where	nr_sequencia	= nr_seq_aval_nut_w;

elsif (ie_informacao_p	= 'NP') then

	select	max(qt_nec_proteica)
	into STRICT	ds_retorno_w
	from	aval_nutricao
	where	nr_sequencia	= nr_seq_aval_nut_w;

elsif (ie_informacao_p	= 'C') then

	select	max(qt_caloria)
	into STRICT	ds_retorno_w
	from	aval_nutricao
	where	nr_sequencia	= nr_seq_aval_nut_w;

elsif (ie_informacao_p	= 'P') then

	select	max(qt_proteina)
	into STRICT	ds_retorno_w
	from	aval_nutricao
	where	nr_sequencia	= nr_seq_aval_nut_w;

end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_aval_nutric (nr_atendimento_p bigint, ie_informacao_p text) FROM PUBLIC;

