CREATE OR REPLACE FUNCTION obter_dados_avf_tipo_nota_item ( nr_sequencia_p bigint, ie_opcao_p text, nr_seq_pergunta_p bigint default null, nr_Seq_resultado_p bigint default null) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
D=	Descricao
N=	Nota
P	Pontos*/
ds_retorno_w			varchar(255);
ds_tipo_nota_w			varchar(255);
vl_tipo_nota_w			double precision;


BEGIN

select	ds_tipo_nota,
	vl_tipo_nota
into STRICT	ds_tipo_nota_w,
	vl_tipo_nota_w
from	avf_tipo_nota_item
where	nr_sequencia = nr_sequencia_p;

if (ie_opcao_p = 'D') then
	ds_retorno_w := ds_tipo_nota_w;
elsif (ie_opcao_p = 'N') then
	ds_retorno_w := vl_tipo_nota_w;
elsif (ie_opcao_p = 'P') then
	select	sum(qt_pontos)
	into STRICT	ds_retorno_w
	from	avf_pergunta c,
		avf_tipo_nota_item b,
		avf_tipo_nota a,
		avf_pergunta_tipo d,
		avf_resultado_item e
	where	a.nr_sequencia = b.nr_seq_tipo_nota
	and	c.nr_seq_tipo_nota = a.nr_sequencia
	and	d.nr_seq_tipo_nota_item = b.nr_Sequencia
	and	c.nr_sequencia 	= d.nr_seq_pergunta
	and	d.nr_seq_pergunta	= e.nr_seq_pergunta
	and	d.nr_seq_tipo_nota_item	= e.nr_seq_nota_item
	and	b.nr_sequencia = nr_sequencia_p
	and	e.nr_seq_pergunta = nr_seq_pergunta_p
	and	e.nr_Seq_resultado = nr_seq_resultado_p;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
