SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_banco_conta ( nr_sequencia_p bigint, ie_operacao_p text) RETURNS varchar AS $body$
DECLARE


ds_resultado_w			varchar(100);

ds_banco_w				Banco.ds_banco%type;
cd_agencia_bancaria_w	banco_estabelecimento.cd_agencia_bancaria%type;
ds_agencia_bancaria_w	agencia_bancaria.ds_agencia_bancaria%type;
cd_conta_w				banco_estabelecimento.cd_conta%type;
ie_digito_conta_w		Banco_Estabelecimento.ie_digito_conta%type;
ie_digito_agencia_w		Agencia_Bancaria.ie_digito%type;
cd_banco_w				Banco_Estabelecimento.cd_banco%type;
cd_moeda_unica_w		Banco_Estabelecimento.cd_moeda%type;
cd_banco_externo_w		Banco.cd_banco_externo%type;
cd_conta_digito_w		varchar(40);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	select 	b.ds_banco,
			a.cd_agencia_bancaria,
			a.cd_conta,
			a.ie_digito_conta,
			a.ie_digito_agencia,
			substr(a.cd_conta || CASE WHEN coalesce(a.ie_digito_conta, '-1')='-1' THEN ''  ELSE '-' END  || a.ie_digito_conta, 1, 40) cd_conta_digito,
			a.cd_banco,
			a.cd_moeda,
			b.cd_banco_externo
	into STRICT 	ds_banco_w,
			cd_agencia_bancaria_w,
			cd_conta_w,
			ie_digito_conta_w,
			ie_digito_agencia_w,
			cd_conta_digito_w,
			cd_banco_w,
			cd_moeda_unica_w,
			cd_banco_externo_w
	FROM banco_estabelecimento_v a
LEFT OUTER JOIN banco b ON (a.cd_banco = b.cd_banco)
WHERE a.nr_sequencia = nr_sequencia_p;
	exception when others then
		ds_resultado_w	:= '';
	end;


	select	max(a.ds_agencia_bancaria)
	into STRICT	ds_agencia_bancaria_w
	from	agencia_bancaria a
	where	a.cd_agencia_bancaria	= cd_agencia_bancaria_w;

if (ie_operacao_p = 'DSB') then
	begin
	ds_resultado_w:= ds_banco_w;
	end;
elsif (ie_operacao_p = 'CDA') then
	begin
	ds_resultado_w := cd_agencia_bancaria_w;
	end;
elsif (ie_operacao_p = 'DSA') then
	begin
	ds_resultado_w := ds_agencia_bancaria_w;
	end;
elsif (ie_operacao_p = 'CDC') then
	begin
	ds_resultado_w := cd_conta_w;
	end;
elsif (ie_operacao_p = 'CDCD') then
	begin
	ds_resultado_w := cd_conta_digito_w;
	end;
elsif (ie_operacao_p = 'CDB') then
	begin
	ds_resultado_w := coalesce(cd_banco_externo_w, cd_banco_w);
	end;
elsif (ie_operacao_p = 'CDMU') then
	begin
	ds_resultado_w := cd_moeda_unica_w;
	end;
elsif (ie_operacao_p = 'CDME') then
	begin
	ds_resultado_w := coalesce(upper(obter_dados_moeda(cd_moeda_unica_w, 1)), 'MXN');
	end;
end if;

end if;

return ds_resultado_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
